mapper.select:
select

  distinct


from operation




  order by ${orderByClause}



    limit ${offset}, ${limit}


    limit ${limit}

mapper.insert:
insert into operation (" operationNumber", chiefSurgeonId, 
  admissionNumber, patientId, operationName, 
  anaesthesiaMode, incisionCondition, " operationDate", 
  operationDuration, surgicalResults, operatingRoomNumber
  )
values (#{operationnumber,jdbcType=INTEGER}, #{chiefsurgeonid,jdbcType=INTEGER}, 
  #{admissionnumber,jdbcType=INTEGER}, #{patientid,jdbcType=INTEGER}, #{operationname,jdbcType=VARCHAR}, 
  #{anaesthesiamode,jdbcType=INTEGER}, #{incisioncondition,jdbcType=VARCHAR}, #{operationdate,jdbcType=VARCHAR}, 
  #{operationduration,jdbcType=VARCHAR}, #{surgicalresults,jdbcType=VARCHAR}, #{operatingroomnumber,jdbcType=INTEGER}
  )
